import React, { useState } from "react";

const Adduser = () => {
	const myStyle = { marginTop: "100px" };
	const [user, setuser] = useState({
		Name: "",
		Phone: "",
		Address: "",
	});
	let name, value;
	const handleuser = (e) => {
		name = e.target.name;
		value = e.target.value;
		setuser({ ...user, [name]: value });
	};
	const postuser = async (e) => {
		e.preventDefault();
		const { Name, Phone, Address } = user;
		const data = await fetch("/adduser", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify({ Name, Phone, Address }),
		});
		const userdata = data.json();
		if(!userdata || userdata.status === 404) {
			window.alert("User Already Exists");
		} else {
			window.alert("User ")
		}
	};
	return (
		<div className="container" style={myStyle}>
			<div className="mb-4" style={{ fontSize: "30px" }}>
				Add a New User
			</div>
			<form>
				<div class="mb-3">
					<i className="fa fa-user fa-lg"></i>
					<label
						class="form-label"
						style={{ marginLeft: "10px", fontSize: "17px" }}
					>
						Name
					</label>
					<input
						name="Name"
						value={user.Name}
						onChange={handleuser}
						class="form-control"
						id="exampleInputEmail1"
						aria-describedby="emailHelp"
					/>
				</div>
				<div class="mb-3">
					<i className="fa fa-phone fa-lg"></i>
					<label
						class="form-label"
						style={{ marginLeft: "10px", fontSize: "17px" }}
					>
						Phone no.
					</label>
					<input
						name="Phone"
						value={user.Phone}
						onChange={handleuser}
						class="form-control"
						id="exampleInputEmail1"
						aria-describedby="emailHelp"
					/>
				</div>
				<div class="mb-3">
					<i className="fa fa-bookmark fa-lg"></i>
					<label
						class="form-label"
						style={{ marginLeft: "10px", fontSize: "17px" }}
					>
						Address
					</label>
					<input
						name="Address"
						value={user.Address}
						onChange={handleuser}
						type="text"
						class="form-control"
						id="exampleInputPassword1"
					/>
				</div>
				<button type="submit" class="btn btn-primary" onClick={postuser}>
					Add User
				</button>
			</form>
		</div>
	);
};

export default Adduser;
