import React, { useState } from "react";

const Addbook = () => {
	const [book, setbook] = useState({
		book_id: "",
		title: "",
		Type: "",
		price: "",
	});
	let name, value;
	const handleBook = (e) => {
		name = e.target.name;
		value = e.target.value;
		setbook({ ...book, [name]: value });
	};
	const myStyle = {
		marginTop: "100px",
	};
    const postbook = async (e) => {
        e.preventDefault();
        const {book_id, title, Type, price} = book;
        const bookData = await fetch("/addbook", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
			}
		});

    }
	return (
		<>
			<div className="container" style={myStyle}>
				<form method="POST">
					<div class="mb-3">
						<i className="fa fa-address-book fa-lg"></i>
						<label
							for="exampleInputEmail1"
							class="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Book ID
						</label>
						<input
							name="book_id"
							value={book.book_id}
							onChange={handleBook}
							class="form-control"
							id="exampleInputEmail1"
							aria-describedby="emailHelp"
						/>
					</div>
					<div class="mb-3">
						<i className="fa fa-book fa-lg"></i>
						<label
							for="exampleInputEmail1"
							class="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Title of the Book.
						</label>
						<input
							name="title"
							value={book.title}
							onChange={handleBook}
							class="form-control"
							id="exampleInputEmail1"
							aria-describedby="emailHelp"
						/>
					</div>
					<div class="mb-3">
						<i className="fa fa-bookmark fa-lg"></i>
						<label
							for="exampleInputPassword1"
							class="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Type of book
						</label>
						<input
							name="Type"
							value={book.Type}
							onChange={handleBook}
							class="form-control"
							id="exampleInputPassword1"
						/>
					</div>
					<div class="mb-3">
						<i className="fa fa-rupee-sign fa-lg"></i>
						<label
							for="exampleInputPassword1"
							class="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Price of book
						</label>
						<input
							name="price"
							value={book.price}
							onChange={handleBook}
							class="form-control"
							id="exampleInputPassword1"
						/>
					</div>
					<button type="submit" class="btn btn-primary" onClick={postbook}>
						Add Book
					</button>
				</form>
			</div>
		</>
	);
};

export default Addbook;
