import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Issue = () => {
	const navigate = useNavigate();
	const [details, setdetails] = useState({
		book_id: "",
		Name: "",
		Phone: "",
	});
	let name, value;
	const handlebook = (e) => {
		name = e.target.name;
		value = e.target.value;
		setdetails({ ...details, [name]: value });
	};
	const postbook = async (e) => {
		e.preventDefault();
		const { book_id, Name, Phone } = details;
		const data = await fetch("/issue", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify({ book_id, Name, Phone }),
		});
        const newData = data.json();
        if(newData.status === 404) {
            window.alert("Book not found.");
            navigate("/addbook");
        } else if(newData.status === 422) {
            window.alert("User not found. Please first register the user to continue.");
            navigate("/adduser");
        } else {
            window.alert(`Book issued to ${Name}`);
            navigate("/adduser");
        }
	};
	return (
		<>
			<div className="container" style={{ marginTop: "100px" }}>
				<div className="mb-4" style={{ fontSize: "30px" }}>
					Enter the required Details here.
				</div>
				<form method="POST">
					<div className="mb-3">
						<i className="fa fa-address-book fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "17px" }}
						>
							Book ID
						</label>
						<input
							name="book_id"
							value={details.book_id}
							onChange={handlebook}
							className="form-control"
						/>
					</div>
					<div className="mb-3">
						<i className="fa fa-book fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "17px" }}
						>
							Name of User
						</label>
						<input
							name="Name"
							value={details.Name}
							onChange={handlebook}
							className="form-control"
						/>
					</div>
					<div className="mb-3">
						<i className="fa fa-bookmark fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "17px" }}
						>
							Phone No.
						</label>
						<input
							name="Phone"
							value={details.Phone}
							onChange={handlebook}
							className="form-control"
						/>
					</div>
					<button type="submit" className="btn btn-primary" onClick={postbook}>
						Issue the Book
					</button>
				</form>
			</div>
		</>
	);
};

export default Issue;
