import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Addbook = () => {
	const navigate = useNavigate();
	const myStyle = { marginTop: "100px" };
	const [book, setbook] = useState({
		book_id: "",
		title: "",
		Type: "",
		price: "",
	});
	let name, value;
	const handleBook = (e) => {
		name = e.target.name;
		value = e.target.value;
		setbook({ ...book, [name]: value });
	};
	const postbook = async (e) => {
		e.preventDefault();
		const { book_id, title, Type, price } = book;
		const bookData = await fetch("/addbook", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify({
				book_id,
				title,
				Type,
				price,
			}),
		});
		const data = bookData.json();
		if (data.status === 422 || !data) {
			window.alert("Invalid details");
		} else {
			window.alert("Book Added successfully");
			navigate("/");
		}
	};
	return (
		<>
			<div className="container" style={myStyle}>
				<div className="mt-2">Add a New Book</div>
				<form method="POST">
					<div className="mb-3">
						<i className="fa fa-address-book fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Book ID
						</label>
						<input
							name="book_id"
							value={book.book_id}
							onChange={handleBook}
							className="form-control"
							id="exampleInputEmail1"
							aria-describedby="emailHelp"
						/>
					</div>
					<div className="mb-3">
						<i className="fa fa-book fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Title of the Book.
						</label>
						<input
							name="title"
							value={book.title}
							onChange={handleBook}
							className="form-control"
							id="exampleInputEmail1"
							aria-describedby="emailHelp"
						/>
					</div>
					<div className="mb-3">
						<i className="fa fa-bookmark fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Type of book
						</label>
						<input
							name="Type"
							value={book.Type}
							onChange={handleBook}
							className="form-control"
							id="exampleInputPassword1"
						/>
					</div>
					<div className="mb-3">
						<i className="fa fa-rupee-sign fa-lg"></i>
						<label
							className="form-label"
							style={{ marginLeft: "10px", fontSize: "22px" }}
						>
							Price of book
						</label>
						<input
							name="price"
							value={book.price}
							onChange={handleBook}
							className="form-control"
							id="exampleInputPassword1"
						/>
					</div>
					<button type="submit" className="btn btn-primary" onClick={postbook}>
						Add Book
					</button>
				</form>
			</div>
		</>
	);
};

export default Addbook;
